mapper.select:
SELECT BNO, WRITER, TITLE, CONTENT, FILENAME, SAVENAME, CREATED, LASTMODIFIED
  FROM BOARD
 WHERE BNO = #{bNo}

mapper.update:
UPDATE BOARD 
   SET TITLE=#{title}, CONTENT = #{content}, LASTMODIFIED = SYSDATE
   		,FILENAME = #{fileName}
   		,SAVENAME = #{saveName}
 WHERE BNO = #{bNo}
